import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import { useState } from "react";
import ExpensesFilter from "./ExpensesFilter";

export default function Expenses({ expenses }) {
  const [filteredYear, setFilteredYear] = useState("2022");

  let filterInfoText = "2023, 2024, 2025 & 2026";

  if (filteredYear === 2023) {
    filterInfoText = "2022, 2024, 2025 & 2026";
  }
  if (filteredYear === 2024) {
    filterInfoText = "2022, 2023, 2025 & 2026";
  }
  if (filteredYear === 2025) {
    filterInfoText = "2022, 2023, 2024 & 2026";
  }
  if (filteredYear === 2026) {
    filterInfoText = "2022, 2023, 2024 & 2025";
  }

  console.log("Changed");

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter
        filteredYear={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      <p>Data for years {filterInfoText} is hidden.</p>
      <ExpenseItem expense={expenses[0]} />
      <ExpenseItem expense={expenses[1]} />
      <ExpenseItem expense={expenses[2]} />
    </Card>
  );
}
